- Tạo database nếu chưa tồn tại
IF NOT EXISTS (SELECT * FROM sys.databases WHERE name = N'MovieTheater')
BEGIN
    CREATE DATABASE MovieTheater;
END;
GO

USE MovieTheater;
GO

-- Tạo bảng Theaters
CREATE TABLE Theaters (
    TheaterId INT PRIMARY KEY IDENTITY(1,1),
    Name NVARCHAR(255),
    TotalRoom INT,
    Location NVARCHAR(255)
);
GO

-- Tạo bảng Room
CREATE TABLE Room (
    RoomId INT PRIMARY KEY IDENTITY(1,1),
    TotalSeat INT,
    Name NVARCHAR(255),
    TheaterId INT,
    FOREIGN KEY (TheaterId) REFERENCES Theaters(TheaterId)
);
GO

-- Tạo bảng Seats
CREATE TABLE Seats (
    SeatId INT PRIMARY KEY IDENTITY(1,1),
    SeatNumber NVARCHAR(10),
    Line NVARCHAR(10),
    RoomId INT,
    FOREIGN KEY (RoomId) REFERENCES Room(RoomId)
);
GO

-- Tạo bảng Movies
CREATE TABLE Movies (
    MovieId INT PRIMARY KEY IDENTITY(1,1),
    Title NVARCHAR(255),
    Description NVARCHAR(MAX),
    Genre NVARCHAR(100),
    ReleaseDate DATE,
    Rating FLOAT,
    Duration INT,
    Director NVARCHAR(255),
    PosterUrl NVARCHAR(255)
);
GO

-- Tạo bảng Showtimes
CREATE TABLE Showtimes (
    ShowtimeId INT PRIMARY KEY IDENTITY(1,1),
    StartTime DATETIME2,
    EndTime DATETIME2,
    SeatStatus NVARCHAR(20),
    Price FLOAT,
    TheaterId INT,
    RoomId INT,
    MovieId INT,
    FOREIGN KEY (TheaterId) REFERENCES Theaters(TheaterId),
    FOREIGN KEY (RoomId) REFERENCES Room(RoomId),
    FOREIGN KEY (MovieId) REFERENCES Movies(MovieId)
);
GO

-- Tạo bảng Users
CREATE TABLE Users (
    UserId INT PRIMARY KEY IDENTITY(1,1),
    Name NVARCHAR(255),
    Password NVARCHAR(255), -- Lưu ý: Nên mã hóa mật khẩu trong thực tế
    Email NVARCHAR(255),
    Phone NVARCHAR(15),
    Role NVARCHAR(50),
    CreateAt DATETIME2,
    Status NVARCHAR(20)
);
GO

-- Tạo bảng Payments
CREATE TABLE Payments (
    PaymentId INT PRIMARY KEY IDENTITY(1,1),
    PaymentStatus NVARCHAR(20),
    Amount FLOAT,
    PaymentTime DATETIME2,
    PaymentMethod NVARCHAR(50),
    UserId INT,
    FOREIGN KEY (UserId) REFERENCES Users(UserId)
);
GO

-- Tạo bảng Tickets
CREATE TABLE Tickets (
    TicketId INT PRIMARY KEY IDENTITY(1,1),
    SeatNumber NVARCHAR(10),
    BookingTime DATETIME2,
    TotalPrice FLOAT,
    PaymentStatus NVARCHAR(20),
    UserId INT,
    ShowtimeId INT,
    PaymentId INT,
    FOREIGN KEY (UserId) REFERENCES Users(UserId),
    FOREIGN KEY (ShowtimeId) REFERENCES Showtimes(ShowtimeId),
    FOREIGN KEY (PaymentId) REFERENCES Payments(PaymentId)
);
GO

-- Tạo stored procedure để cập nhật PaymentStatus sau 10 phút
CREATE PROCEDURE UpdateTicketStatus
    @TicketId INT
AS
BEGIN
    UPDATE Tickets
    SET PaymentStatus = N'cancel'
    WHERE TicketId = @TicketId AND PaymentStatus = N'pending';
END;
GO

-- Tạo trigger để gọi stored procedure sau khi insert vào Tickets
CREATE TRIGGER AfterInsertTicket
ON Tickets
AFTER INSERT
AS
BEGIN
    DECLARE @TicketId INT;
    SELECT @TicketId = TicketId FROM inserted;

    -- Tạo công việc SQL Agent để chạy sau 10 phút
    -- Lưu ý: Cần cấu hình SQL Server Agent để chạy công việc này
    DECLARE @JobName NVARCHAR(128) = N'UpdateTicket_' + CAST(@TicketId AS NVARCHAR(10));
    DECLARE @StepName NVARCHAR(128) = N'UpdateTicketStep_' + CAST(@TicketId AS NVARCHAR(10));
    DECLARE @Command NVARCHAR(4000) = N'EXEC UpdateTicketStatus N' + CAST(@TicketId AS NVARCHAR(10));

    -- Thêm công việc vào SQL Server Agent
    EXEC msdb.dbo.sp_add_job
        @job_name = @JobName;

    EXEC msdb.dbo.sp_add_jobstep
        @job_name = @JobName,
        @step_name = @StepName,
        @subsystem = N'TSQL',
        @command = @Command;

    EXEC msdb.dbo.sp_add_jobschedule
        @job_name = @JobName,
        @name = N'OneTimeSchedule',
        @freq_type = 1, -- Chạy một lần
        @active_start_date = NULL,
        @active_start_time = NULL,
        @freq_interval = 1,
        @freq_subday_type = 4, -- Phút
        @freq_subday_interval = 10; -- Sau 10 phút

    EXEC msdb.dbo.sp_add_jobserver
        @job_name = @JobName;

    EXEC msdb.dbo.sp_start_job
        @job_name = @JobName;
END;
GO

